  <script>
    var tp_chameleon_url_stylesheet = 'http://c2a2v9c8.stackpathcdn.com/demo-test-prep/wp-content/themes/wordpress-lms/style.css';var tp_chameleon_url_admin_ajax = 'http://wordpresslms.thimpress.com/demo-test-prep/wp-admin/admin-ajax.php';var tp_chameleon_wp_nonce = '3f37da2d4d';var tp_chameleon_primary_color = 'rgb(0, 0, 0)';var tp_chameleon_selector_wrapper_box = '.content-pusher';var tp_chameleon_class_boxed = 'boxed-area';var tp_chameleon_src_patterns = 'http://wordpresslms.thimpress.com/demo-test-prep/wp-content/plugins/tp-chameleon/images/patterns/';var tp_chameleon_setting = {layout : tp_chameleon_getCookie('tp_chameleon_layout'),pattern_type : tp_chameleon_getCookie('tp_chameleon_pattern_type'),pattern_src : tp_chameleon_getCookie('tp_chameleon_pattern_src'),primary_color : tp_chameleon_getCookie('tp_chameleon_primary_color'),primary_color_rgb: tp_chameleon_getCookie('tp_chameleon_primary_color_rgb'),body_font : tp_chameleon_getCookie('tp_chameleon_body_font'),body_font_code : tp_chameleon_getCookie('tp_chameleon_body_font_code'),heading_font : tp_chameleon_getCookie('tp_chameleon_heading_font'),heading_font_code: tp_chameleon_getCookie('tp_chameleon_heading_font_code')};<!---->//if (top !== self) top.location.replace(self.location.href);//var tp_chameleon_site_url = '//wordpresslms.thimpress.com/demo-test-prep';function tp_chameleon_setCookie(cname, cvalue, exdays) {var d = new Date();d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));var expires = "expires=" + d.toUTCString();document.cookie = cname + "=" + cvalue + "; " + expires;}function tp_chameleon_deleteCookie(cname) {var d = new Date();d.setTime(d.getTime() + (24 * 60 * 60 * 1000));var expires = "expires=" + d.toUTCString();document.cookie = cname + "=; " + expires;}function tp_chameleon_deleteAllCookie() {var all_cookie = ['tp_chameleon_layout','tp_chameleon_pattern_type','tp_chameleon_pattern_src','tp_chameleon_primary_color','tp_chameleon_primary_color_rgb','tp_chameleon_body_font','tp_chameleon_body_font_code','tp_chameleon_heading_font','tp_chameleon_heading_font_code'];for (var i = 0; i < all_cookie.length; i++) {tp_chameleon_deleteCookie(all_cookie[i]);}}function tp_chameleon_getCookie(cname) {var name = cname + "=";var ca = document.cookie.split(';');for (var i = 0; i < ca.length; i++) {var c = ca[i];while (c.charAt(0) == ' ') c = c.substring(1);if (c.indexOf(name) == 0) return c.substring(name.length, c.length);}return '';}function tp_chameleon_set_first_visit() {tp_chameleon_setCookie('tp_chameleon_first_visit', 1, 1);}function tp_chameleon_check_first_visit() {return !(tp_chameleon_getCookie('tp_chameleon_first_visit') == '1' );}jQuery(document).ready(function ($) {var $preview = $('.tp-preview-images');$('.tp_demo').hover(function (event) {var url_prewview = $(this).attr('data-preview');if (url_prewview) {$preview.find('img').attr('src', url_prewview);$preview.show();}},function () {$preview.hide();});$('.tp_demo').mousemove(function (event) {var y = (event.clientY);$preview.css('top', y - 250);});function tp_chameleon_open() {tp_chameleon_set_first_visit();$('#tp_style_selector').addClass('show').animate({right: '0px'}, 'medium');$('#tp_style_selector .open').hide();$('#tp_style_selector .close').show();}function tp_chameleon_close() {$('#tp_style_selector').removeClass('show').animate({right: '-300px'}, 'medium');$('#tp_style_selector .close').hide();$('#tp_style_selector .open').show();}function tp_change_background_pattern(url_pattern) {var $body = $('body');$body.removeClass('tp_background_image');$body.addClass('tp_background_pattern');$body.css('background-image', 'url("' + url_pattern + '")')}function tp_change_background_image(url_image) {var $body = $('body');$body.removeClass('tp_background_pattern');$body.addClass('tp_background_image');$body.css('background-image', 'url("' + url_image + '")')}function tp_chameleon_change_layout_wide() {tp_chameleon_setCookie('tp_chameleon_layout', 'wide', 1);var $body = $('body');$('.tp-change-layout').removeClass('active');$('.tp-change-layout.layout-wide').addClass('active');$('#tp_style_selector .boxed-mode').slideUp(300);$(tp_chameleon_selector_wrapper_box).removeClass(tp_chameleon_class_boxed);$body.css('background-image', 'none');}function tp_chameleon_change_layout_boxed() {tp_chameleon_setCookie('tp_chameleon_layout', 'boxed', 1);$('.tp-change-layout').removeClass('active');$('.tp-change-layout.layout-boxed').addClass('active');$('#tp_style_selector .boxed-mode').slideDown(300);$(tp_chameleon_selector_wrapper_box).addClass(tp_chameleon_class_boxed);}function tp_chameleon_change_background_pattern(pattern_src) {tp_chameleon_setCookie('tp_chameleon_pattern_src', pattern_src, 1);tp_chameleon_setCookie('tp_chameleon_pattern_type', 'pattern', 1);var pattern_url = tp_chameleon_src_patterns + pattern_src;tp_change_background_pattern(pattern_url);}function tp_chameleon_change_background_image(pattern_src) {tp_chameleon_setCookie('tp_chameleon_pattern_src', pattern_src, 1);tp_chameleon_setCookie('tp_chameleon_pattern_type', 'image', 1);var pattern_url = tp_chameleon_src_patterns + pattern_src;tp_change_background_image(pattern_url);}var $body_font = '<style id=tp_chameleon_body_font></style>';var $heading_font = '<style id=tp_chameleon_heading_font></style>';var $stylesheet = '<link id=tp_chameleon_stylesheet type=text/css rel=stylesheet>';var $tp_head = $('head');$tp_head.append($stylesheet);var $tp_body = $('body');$tp_body.append($body_font);$tp_body.append($heading_font);if (tp_chameleon_setting.layout == 'wide') {tp_chameleon_change_layout_wide();}if (tp_chameleon_setting.layout == 'boxed') {tp_chameleon_change_layout_boxed();if (tp_chameleon_setting.pattern_type == 'pattern' && tp_chameleon_setting.pattern_src != '') {tp_chameleon_change_background_pattern(tp_chameleon_setting.pattern_src);$('.tp_pattern[data-src="' + tp_chameleon_setting.pattern_src + '"]').addClass('active');}if (tp_chameleon_setting.pattern_type == 'image' && tp_chameleon_setting.pattern_src != '') {tp_chameleon_change_background_image(tp_chameleon_setting.pattern_src);$('.tp_image[data-src="' + tp_chameleon_setting.pattern_src + '"]').addClass('active');}}$('.tp-chameleon-clear').click(function (event) {event.preventDefault();tp_chameleon_deleteAllCookie();document.location.reload();});$('.tp-btn.tp-change-layout').click(function (event) {event.preventDefault();if ($(this).hasClass('layout-wide')) {tp_chameleon_change_layout_wide();} else {tp_chameleon_change_layout_boxed();}});$('.tp_pattern').click(function (event) {event.preventDefault();$('.tp_pattern').removeClass('active');$('.tp_image').removeClass('active');$(this).addClass('active');var pattern_src = $(this).attr('data-src');tp_chameleon_change_background_pattern(pattern_src);});$('.tp_image').click(function (event) {event.preventDefault();$('.tp_pattern').removeClass('active');$('.tp_image').removeClass('active');$(this).addClass('active');var pattern_src = $(this).attr('data-src');tp_chameleon_change_background_image(pattern_src);});/** * Open/Close box */$('#tp_style_selector .close').click(function (e) {e.preventDefault();tp_chameleon_close();});$('#tp_style_selector .open').click(function (e) {e.preventDefault();tp_chameleon_open();});$(window).load(function () {var $btn = $('.tp-chameleon-btn-buy');$btn.animate({bottom: $btn.attr('data-bottom')}, 500);//Set view-more-slider button$('.view-more-slider .text:last-child').hide();setInterval(function () {$('.view-more-slider .text:first-child').hide();$('.view-more-slider .text:last-child').show();setTimeout(function () {$('.view-more-slider .text:first-child').show();$('.view-more-slider .text:last-child').hide();}, 2000);}, 4500);});/** * Check firt visit */if (tp_chameleon_check_first_visit()) {setTimeout(tp_chameleon_open, 5000);} else {$('#tp_style_selector').click(function (event) {tp_chameleon_set_first_visit();});}//remove iframe// if ( (!$('body').hasClass('logged-in')) && (!$('body').hasClass('lp-learning'))) {// if (top !== self) {// top.location.replace(self.location.href);// }// } try{ if (top !== self) top.location.replace(self.location.href); }catch(ex){console.log('Redirecting error')} });
